/*! minireset.css v0.0.3 | MIT License | github.com/jgthms/minireset.css */html,body,p,ol,ul,li,dl,dt,dd,blockquote,figure,fieldset,legend,textarea,pre,iframe,hr,h1,h2,h3,h4,h5,h6{margin:0;padding:0}h1,h2,h3,h4,h5,h6{font-size:100%;font-weight:normal}ul{list-style:none}button,input,select,textarea{margin:0}html{box-sizing:border-box}*,*:before,*:after{box-sizing:inherit}img,embed,iframe,object,audio,video{height:auto;max-width:100%}iframe{border:0}table{border-collapse:collapse;border-spacing:0}td,th{padding:0;text-align:left}article,footer,header,main,menu,nav,section,summary{display:block;}

/*	garamondtitalic	*/
@font-face {
    font-family: 'garamondtitalic';
    src: url('{_root}data/font/garamondt-italic-webfont.woff2') format('woff2'),
         url('{_root}data/font/garamondt-italic-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

/*	garamondt	*/
@font-face {
    font-family: 'garamondtregular';
    src: url('{_root}data/font/garamondt-regular-webfont.woff2') format('woff2'),
         url('{_root}data/font/garamondt-regular-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

/*	basteleurmoonlight	*/
@font-face {
    font-family: 'basteleurmoonlight';
    src: url('{_root}data/font/basteleur-moonlight-webfont.woff2') format('woff2'),
         url('{_root}data/font/basteleur-moonlight-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

/*	basteleurbold	*/
@font-face {
    font-family: 'basteleurbold';
    src: url('{_root}data/font/basteleur-bold-webfont.woff2') format('woff2'),
         url('{_root}data/font/basteleur-bold-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

html    {font-size:18px; scroll-behavior:smooth;}
body	{background:#000; font-family:'garamondtregular',sans-serif; font-size:18px; font-weight:400; line-height:normal; color:#fff; max-width:100%; overflow-x:hidden; padding:0; margin:0;}
.fog	{width:100%; height:100vh; background:url('{_root}data/fog_back.png'); animation:flay 100s linear infinite;}
.over	{background-color:#000; margin:0 auto; padding:0; width:100%; background:linear-gradient(0deg,#020024e0 23%,#160a2b5e 64%,#3f2d488f 100%); animation:play 300s ease-in infinite; background-size:600%; height:100vh;}
@keyframes play {0% {background-position:0% 50%;} 50% {background-position:100% 50%;} 100% {background-position:0% 50%;}}
@keyframes flay {0% {background-position:0px 0px;} 100% {background-position:-1920px 0px;}}
h1		{font-size:1.5em; font-weight:normal;}
h2		{font-size:1.5em; font-weight:normal;}
h3      {font-size:2.0em; font-weight:500; color:#736b62;}
b		{font-weight:700;}
i		{font-style:italic;}
a		{color:#111; text-decoration:none; transition:all 0.5s ease-out;}

.wrap	{margin:0 auto; padding:0; position:relative; overflow:hidden;}
.img	{position:absolute; top:0; left:0; right:0; margin:auto; border-radius:6px 6px 0 0; padding:0; background:url('{_root}data/grief/grief1.jpg') center center no-repeat; height:100vh; width:50vw; filter:grayscale(1) sepia(0.5) brightness(1.3) invert(0.8); opacity:0.5; animation:glitch 2s infinite linear alternate-reverse;}
@keyframes glitch {0% {transform:translate(1px, 1px) rotate(0deg);} 50% {transform:translate(-1px, 2px) rotate(-1deg);} 100% {transform:translate(1px, -2px) rotate(-1deg);}}


.talk_wrap	{position:relative; display:flex; align-items:flex-end; height:100vw; width:100vw; max-width:100vh; max-height:100vh; min-height:960px; min-width:480px; margin:0 auto; padding-bottom:3em;}
.talky	{border-radius:6px; border:4px double #3b3b51; padding:20px 30px 30px 30px; background-color:#000000bd; width:50vh; margin:0 auto;}
.name	{color:#d0ce55; font-size:32px; font-family:'garamondtitalic',sans-serif;}
.quest	{color:#fff; font-size:26px;}
.read_wrap	{text-align:right;}
.read	{color:#b2b6f2; font-size:26px; margin-top:10px; background:#2f2753; display:inline-block; padding:5px 25px; border-radius:6px; border:4px double #000; text-transform:uppercase; font-family:'garamondtitalic',sans-serif;}


*:focus {outline:2px solid transparent;}
*:focus:not(:focus-visible) {outline:2px solid transparent; box-shadow:none;}
*:focus-visible		{outline: 3px solid #918170;}	/* for overriding FF's dotted outline*/
input:focus			{outline:2px solid transparent; box-shadow: 0 0 0 2px #918170;}
input:focus-visible {outline:2px solid #918170; background-color:#fff; border-color:#918170;}

.aria-invisible {border:0; clip:rect(0 0 0 0); height:1px; margin:-1px; overflow:hidden; padding:0; position:absolute; width:1px;}

.skip-link		{background:#665645; border-radius:0 0 6px 6px; color:#fff; height:30px; left:50%; padding:6px; position:absolute; transform:translateY(-100%); transition:transform 0.3s;}
.skip-link:focus{transform:translateY(0%);}



@media (min-width: 360px) and (max-width: 767.98px) {
	.img	{width:100vw; height:55vh; background-size:contain;}
	.talk_wrap	{min-width:360px; min-height:800px;}
	.fog	{background-size:200%;}
}

@media (min-width: 768px) and (max-width: 991.98px) {
	.img	{width:100vw; background-size:contain;}
}
