/*! minireset.css v0.0.3 | MIT License | github.com/jgthms/minireset.css */html,body,p,ol,ul,li,dl,dt,dd,blockquote,figure,fieldset,legend,textarea,pre,iframe,hr,h1,h2,h3,h4,h5,h6{margin:0;padding:0}h1,h2,h3,h4,h5,h6{font-size:100%;font-weight:normal}ul{list-style:none}button,input,select,textarea{margin:0}html{box-sizing:border-box}*,*:before,*:after{box-sizing:inherit}img,embed,iframe,object,audio,video{height:auto;max-width:100%}iframe{border:0}table{border-collapse:collapse;border-spacing:0}td,th{padding:0;text-align:left}article,footer,header,main,menu,nav,section,summary{display:block;}

/*	garamondtitalic	*/
@font-face {
    font-family: 'garamondtitalic';
    src: url('{_root}data/font/garamondt-italic-webfont.woff2') format('woff2'),
         url('{_root}data/font/garamondt-italic-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

/*	garamondt	*/
@font-face {
    font-family: 'garamondtregular';
    src: url('{_root}data/font/garamondt-regular-webfont.woff2') format('woff2'),
         url('{_root}data/font/garamondt-regular-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

/*	basteleurmoonlight	*/
@font-face {
    font-family: 'basteleurmoonlight';
    src: url('{_root}data/font/basteleur-moonlight-webfont.woff2') format('woff2'),
         url('{_root}data/font/basteleur-moonlight-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

/*	basteleurbold	*/
@font-face {
    font-family: 'basteleurbold';
    src: url('{_root}data/font/basteleur-bold-webfont.woff2') format('woff2'),
         url('{_root}data/font/basteleur-bold-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

html    {font-size:18px; scroll-behavior:smooth;}
body	{background:#000; font-family:'garamondtregular',sans-serif; font-size:18px; font-weight:400; line-height:normal; color:#fff; max-width:100%; overflow-x:hidden; padding:0; margin:0;}
h1		{font-size:1.5em; font-weight:normal;}
h2		{font-size:1.5em; font-weight:normal;}
h3      {font-size:2.0em; font-weight:500; color:#736b62;}
b		{font-weight:700;}
i		{font-style:italic;}
a		{color:#111; text-decoration:none; transition:all 0.5s ease-out;}

/*	Chrome scrollbar	*/
::-webkit-scrollbar			{width:9px;}
::-webkit-scrollbar-track	{background:transparent;}
::-webkit-scrollbar-thumb	{background-color:rgba(155, 155, 155, 0.5); border-radius:20px; border:transparent;}

*:focus {outline:2px solid transparent;}
*:focus:not(:focus-visible) {outline:2px solid transparent; box-shadow:none;}
*:focus-visible		{outline: 3px solid #918170;}	/* for overriding FF's dotted outline*/
input:focus			{outline:2px solid transparent; box-shadow: 0 0 0 2px #918170;}
input:focus-visible {outline:2px solid #918170; background-color:#fff; border-color:#918170;}
.aria-invisible {border:0; clip:rect(0 0 0 0); height:1px; margin:-1px; overflow:hidden; padding:0; position:absolute; width:1px;}
.skip-link		{background:#665645; border-radius:0 0 6px 6px; color:#fff; height:30px; left:50%; padding:6px; position:absolute; transform:translateY(-100%); transition:transform 0.3s;}
.skip-link:focus{transform:translateY(0%);}

.main_wrap	{margin:0 auto; padding:0; position:relative; overflow:hidden; overflow-y:auto; height:100vh;}
.wrap		{margin:0 auto; padding:0; position:relative; overflow:hidden;}

/*	main background	*/
.fog	{width:100%; height:100vh; background:url('{_root}data/fog_back.png'); animation:flay 100s linear infinite;}
.over	{background-color:#000; margin:0 auto; padding:0; width:100%; background:linear-gradient(0deg,#020024e0 23%,#160a2b5e 64%,#3f2d488f 100%); height:100vh;}
@keyframes play {0% {background-position:0% 50%;} 50% {background-position:100% 50%;} 100% {background-position:0% 50%;}}
@keyframes flay {0% {background-position:0px 0px;} 100% {background-position:-1920px 0px;}}
@keyframes slow_back {0%,50% {background:transparent;} 100% {background:url('{_root}data/fog_back.png');}}


/*	background stars	*/
.stars	{background-color:white; height:3px; width:3px; border-radius:50%; box-shadow:1px 0px 20px #fff;}
.star-1 {position:absolute; top:15vh; left:35%; animation:twi1 5s ease-in infinite;}
.star-2 {position:absolute; top:11vh; left:65%; animation:twi2 3s ease-in infinite;}
.star-3 {position:absolute; top:5vh; left:50%; animation:twi3 2s ease-in infinite;}
.star-4 {position:absolute; top:35vh; left:5%; animation:twi1 1s ease-in infinite;}
.star-5 {position:absolute; top:45vh; left:95%; animation:twi2 4s ease-in infinite;}
.star-6 {position:absolute; top:60vh; left:80%; animation:twi3 1.5s ease-in infinite;}
.star-7 {position:absolute; top:22vh; left:30%; animation:twi3 2.5s ease-in infinite;}
.star-8 {position:absolute; top:55vh; left:45%; animation:twi1 7s ease-in infinite;}
.star-9 {position:absolute; top:40vh; left:70%; animation:twi2 6s ease-in infinite;}
.star-10 {position:absolute; top:70vh; left:10%; animation:twi3 2.5s ease-in infinite;}
.star-11 {position:absolute; top:3vh; left:25%; animation:twi1 5s ease-in infinite;}
.star-12 {position:absolute; top:7vh; left:5%; animation:twi2 3s ease-in infinite;}
.star-13 {position:absolute; top:20vh; left:80%; animation:twi3 2s ease-in infinite;}
.star-14 {position:absolute; top:30vh; left:35%; animation:twi1 1s ease-in infinite;}
.star-15 {position:absolute; top:31vh; left:87%; animation:twi2 4s ease-in infinite;}
.star-16 {position:absolute; top:50vh; left:40%; animation:twi3 1.5s ease-in infinite;}
.star-17 {position:absolute; top:12vh; left:20%; animation:twi3 2.5s ease-in infinite;}
.star-18 {position:absolute; top:27vh; left:61%; animation:twi1 7s ease-in infinite;}
.star-19 {position:absolute; top:66vh; left:50%; animation:twi2 6s ease-in infinite;}
.star-20 {position:absolute; top:42vh; left:17%; animation:twi3 2.5s ease-in infinite;}

@keyframes twi1 {0% {transform:scale(0.3); opacity:0.3;} 40% {transform:scale(0.3); opacity:0.3;} 50% {transform:scale(1); opacity:0.3;} 60% {transform:scale(0.3); opacity:0.3;} 100% {transform:scale(0.3); opacity:0.3;}}
@keyframes twi2 {0% {transform:scale(0.5); opacity:0.5;} 40% {transform:scale(0.5); opacity:0.5;} 50% {transform:scale(1); opacity:0.5;} 60% {transform:scale(0.5); opacity:0.5;} 100% {transform:scale(0.5); opacity:0.5;}}
@keyframes twi3 {0% {transform:scale(0.1); opacity:0.4;} 40% {transform:scale(0.1); opacity:0.4;} 50% {transform:scale(1); opacity:0.4;} 60% {transform:scale(0.1); opacity:0.4;} 100% {transform:scale(0.1); opacity:0.4;}}

/*	landing page	*/
.woe		{width:10vw; height:25vh; margin:0 auto; border:6px double #2f2753; border-radius:2px; background:url('{_root}data/woe.jpg') center center no-repeat; background-size:cover;}
.title_wrap	{margin:0 auto; text-align:center; height:100vh; width:100vw; display:flex; flex-direction:column; justify-content:space-evenly;}
.title		{position:relative; color:#d0ce55; font-size:132px; font-family:'basteleurmoonlight',serif; text-shadow:1px -2px 17px #5557d0;}
.title.d 	{margin-left:-0.5em; transform:skew(-5deg);}
.title.p	{line-height:0.45em; transform:skew(8deg); padding-bottom:15px;}
.title.p:hover	{animation:blur 1s 1, glitch 0.1s ease-out infinite; color:#9e55d0;}
.title.s 	{margin-left:2.5em; transform:skew(-13deg); font-size:105px;}
.ai			{font-family:monospace,monospace; transform:skew(-8deg); display:inline-block; animation:blur 10s infinite; color:#2d78c8; font-size:100px; letter-spacing:-4px;}
span.sub	{color:#1d02a2; filter:blur(3px); animation:fade 2s ease-in infinite;}
.cred_wrap	{margin:0 auto; margin:0 2.5em;}
.cred		{}
.cred_link	{color:#b2b6f2; font-size:20px; margin-top:10px; display:inline-block; padding:5px 15px; text-transform:uppercase; transition:all 0.3s linear;}
.cred_link:hover, .cred_link:focus, .cred_link:active {transform:skewX(-20deg);}
.go:before,
.go:after	{content:"\263E"; color:#000; position:absolute; margin:-40px; width:30px; height:30px; background-color:#2f2753; border:4px double #000;}
.go:before	{top:50px; left:-10px; transform:rotate(45deg);}
.go:after	{bottom:50px; right:-10px; transform:rotate(-134deg);}
.go:hover:before, .go:hover:after,
.go:focus:before, .go:focus:after,
.go:active:before, .go:active:after	{background:#361EA5;}

/*	credit page	*/
.fund_top	{font-size:21px; font-family:'garamondtitalic',serif; padding-bottom:20px; text-align:center;}
.fund_top span	{color:#524D59;}
.fund_line	{border-bottom:4px double #766aac; padding-bottom:20px;}
.fund_info	{margin-top:30px; font-size:20px; line-height:1.25em;}
.fund_wrap	{display:flex; justify-content:space-between; margin-top:20px; margin-bottom:2em;}
.fund span	{font-size:16px; text-align:left; display:inline-block; padding:0 40px 10px 0; line-height:1.25em;}
.fund_img_wrap	{margin:0 auto; margin-bottom:30px; text-align:center;}
.fund_img_wrap span	{display:inline-block; font-size:18px; margin-top:10px;}
.fund_img_a	{background:url('{_root}data/Priyanka_Prekshaa.jpg')center no-repeat; height:360px; width:480px; margin:0 auto;}
.fund_img_b	{background:url('{_root}data/wetgenes.jpg')center no-repeat; height:349px; width:480px; margin:0 auto; margin-top:30px;}

/*	ghost letters for title	*/
#letters	{position:absolute; top:0em; left:0em; right:0; display:flex; justify-content:center; color:#eaeaea; font-size:132px; font-family:'basteleurmoonlight',serif; filter:blur(2px); animation:fade 5s ease-in infinite;}
#letters .let	{animation:up 10s infinite linear; &:nth-child(2){animation-delay:2s;} &:nth-child(3){animation-delay:1.4s;} &:nth-child(4){animation-delay:0.8s;} &:nth-child(5){animation-delay:0.1s;}}
#letters.sea	{font-size:105px;}
#letters.sea .let	{animation:up 10s infinite linear; &:nth-child(2){animation-delay:1.6s;} &:nth-child(3){animation-delay:2.1s;} &:nth-child(4){animation-delay:0.4s;} &:nth-child(5){animation-delay:0.8s;} &:nth-child(6){animation-delay:0.2s;}}

@keyframes up {0% {transform:translate(0,0) skew(0);} 20% {transform:translate(0,-2%) skew(-5deg);} 40% {transform:translate(0,-8%) skew(8deg);} 60% {transform:translate(0,-8%) skew(-7deg);} 80% {transform:translate(0,2%) skew(4deg);} 100% {transform:translate(0,0) skew(0);}}
@keyframes fade {0% {opacity:1;} 70% {opacity:0.5;} 100% {opacity:1;}}

/*	conversation boxes	*/
.talk_wrap	{position:relative; display:flex; align-items:flex-end; height:100vw; width:100vw; max-width:100vh; max-height:100vh; min-height:100vh; min-width:480px; margin:0 auto; padding-bottom:1em;}
.talky	{border-radius:6px; border:4px double #3b3b51; padding:20px 30px 30px 30px; background-color:#000000bd; width:80vh; margin:0 auto; box-shadow:1px 1px 20px #00000078;}
.name	{color:#d0ce55; font-size:28px; line-height:2em; font-family:'basteleurmoonlight',sans-serif;}
.grief	{color:#897097;}
.guilt	{color:#B357E6;}
.shame	{color:#EB4A9B;}
.fear	{color:#CCE657;}
.quest	{color:#fff; font-size:30px; line-height:1.25em; margin-bottom:1em;}
.quest span	{display:inline-block; font-style:italic; line-height:1.25em;}
/*	question spans used for mobile	*/
.quest span.ask	{font-size:26px;}
/*	paired with emotion and fading	*/
.quest span.fae	{font-family:'basteleurmoonlight',serif; font-size:24px; font-style:initial; text-shadow:1px 1px 10px #6c77b3; animation:fade 5s ease-in-out infinite;}
/*	uppercase	*/
.quest span.cap	{text-transform:uppercase;}
/*	quoted answers	*/
.quest span.quo	{color:#89b4db; padding:0 10px 0 5px; display:inline; font-size:28px;}
/*	red and fading	*/
.quest span.sac	{color:#FF3F6A; font-family:'basteleurmoonlight',serif; font-size:24px; font-style:initial; animation:fade 5s ease-in-out infinite;}
/*	trembling light green and fading	*/
.quest span.emo	{color:#57E6C1; font-style:initial; animation:buzz 0.1s linear infinite;}
/*	yellow voice and fading	*/
.quest span.voi	{color:#d0ce55; font-family:'basteleurmoonlight',serif; font-size:24px; font-style:initial; animation:fade 5s ease-in-out infinite;}
/*	light grey letters spaced	*/
.quest span.wea	{color:#888; font-weight:bold; font-size:30px; letter-spacing:4px;}
/*	bright pink letters spaced	*/
.quest span.viv	{color:#FF64E5; font-weight:bold; font-size:30px; letter-spacing:4px; text-shadow:1px 1px 5px #0064ff;}
/*	small red and pulsing	*/
.quest span.warn{color:#E71211; font-family:'garamondtitalic',serif; font-style:initial; font-weight:bold; font-size:28px; animation:pulse 3s ease-in-out 1;}
/*	white glow and pulsing	*/
.quest span.pul	{color:#7CCCE7; font-style:initial; font-size:30px; text-shadow:1px 1px 5px #fff; animation:pulse 3s ease-in-out infinite;}
/*	white glow and blinking	*/
.quest span.bli	{color:#fff; font-style:initial; font-size:30px; text-shadow:1px 1px 5px #fff; animation:fade 0.1s infinite;}
/*	light blue fast fading	*/
.quest span.won	{color:#1176E7; font-style:initial; font-weight:bold; font-size:30px; animation:fade 0.1s ease-in-out infinite;}
/*	white glowing and fading	*/
.quest span.glo	{color:#fff; font-style:initial; font-size:30px; text-shadow:1px 1px 5px #fff; animation:fade 3s ease-in-out infinite;}

.read_wrap	{text-align:center; margin:0 auto; width:60%;}
.read		{color:#b2b6f2; font-size:26px; margin:0 auto; background:#2f2753; display:block; padding:15px; border-radius:6px; border:4px double #000; text-transform:uppercase; font-family:'garamondtitalic',sans-serif; animation:buzz 0.15s linear infinite;}
.read:hover	{background:#361EA5;}
.read:hover, .read:focus, .read:active {animation:buzz 0.25s linear infinite;}
@keyframes buzz	{50% {transform:translateX(3px) rotate(2deg);} 100% {transform:translateX(-3px) rotate(-2deg);}}
@keyframes pulse{25% {transform:scale(1.1);} 75% {transform:scale(0.9);}}
@keyframes blur	{25% {filter:blur(15px);} 100% {filter:blur(0px);}}

.say_wrap	{text-align:center; margin-top:30px; display:flex; align-items:center; justify-content:space-evenly; animation:show 1s steps(40,end);}
.say		{color:#b2b6f2; font-size:22px; line-height:1.35em; background:#2f2753; display:inline-block; padding:20px; border-radius:6px; border:4px double #000; font-family:monospace, monospace; transition-duration:0.3s; transition-property:transform; position:relative;}
.pick		{font-size:18px; line-height:1.25em;}
.say:hover	{background:#361EA5; background:linear-gradient(196deg,#361EA5 78%,#A500BF 100%);}
.say:hover, .say:focus, .say:active {transform:scale(1.05);}
.say:before,
.say:after	{content:"\263E"; color:#000; position:absolute; margin:-40px; width:30px; height:30px; background-color:#2f2753; border:4px double; border-color:#000 transparent transparent #000;}
.say:before	{top:25px; left:35%; transform:rotate(45deg);}
.say:after	{bottom:24px; right:35%; transform:rotate(-134deg);}
.say:hover:before, .say:hover:after,
.say:focus:before, .say:focus:after,
.say:active:before, .say:active:after	{background:#361EA5;}

.arrow_r	{width:40px; height:40px; border:20px solid; border-color:#fff transparent transparent #fff; transform:rotate(134deg); display:block;}
.arrow_l	{width:40px; height:40px; border:20px solid; border-color:#fff transparent transparent #fff; transform:rotate(-45deg); display:block;}
.arrow_r:hover,
.arrow_l:hover	{cursor:pointer; box-shadow:-5px -5px 5px #FFFFFF78;}

/*	candle	*/
.candle_wrap	{display:flex; position:absolute; top:0; right:0; left:0; margin:0 auto; bottom:0; width:100%; height:100%; max-height:100vh; min-height:100vh; overflow:hidden;}
.candle	{width:34px; margin:30px auto 0 auto; position:relative; height:60%; align-self:flex-end; animation:blink 0.1s infinite;}
.flame	{width:20px; height:150px; margin:0px auto; position:relative; animation:move 3s infinite, move-left 3s infinite; transform-origin:50% 90%;}
.flame .top {width:20px; height:100%; position:absolute; top:0; left:0; background:white; border-top-left-radius:500%; border-bottom-left-radius:50px; border-top-right-radius:500%; border-bottom-right-radius:50px; transform:skewY(-10deg); box-shadow:0 0px 0px 3px white, 0 -20px 1px 4px white, 0 -25px 2px 3px gold, 0 -30px 5px 4px #ff6a00, 0 0px 150px 10px #ff6a00, 0 -10px 2px 4px white, 0 -5px 3px 3px white; animation:flame-up 4s infinite;}
.flame .shadows {position:absolute; left:50%; top:0; width:1px; height:60px; border-radius:50%; box-shadow:0 5px 20px 15px gold, 0 0px 100px 20px #ff6a00, 0 15px 50px 15px #ff6a00, 5px 30px 5px 13px #ff6a00, 5px 50px 5px 13px #ff6a00, 0 75px 50px 30px black;}
.flame .bottom {transform:scale(0.9); position:absolute; bottom:6px; left:9px; width:1px; height:8px; border-radius:1%; background:#2c2b39; box-shadow:0 6px 10px 12px rgba(60, 76, 125, 0.3), 0 0px 4px 8px #2c2b39, 0 -12px 10px 8px rgba(255, 106, 0, 0.5), 0 5px 7px 12px #2c2b39, 0 -3px 10px 12px #2c2b39, 5px -10px 10px 5px red, 0 -15px 10px 10px gold, 5px -25px 10px 5px gold, 0 2px 5px 10px #30537d, 0 -2px 2px 14px #76daff, 0 2px 10px 12px #76daff;}
.wax {position:relative; top:15px; width:100%; height:100%; background:linear-gradient(to bottom,#ffd7ae 0px,#f7a53d 20px,#625234 50px); filter:progid:DXImageTransform.Microsoft.gradient(startColorstr="#ff9224", endColorstr="#58523a",GradientType=0); border-top-left-radius:10px; border-top-right-radius:10px; box-shadow:inset 0 7px 12px -2px #fff970, inset 0 9px 57px -3px rgba(72,68,63,0.4),inset 0 -5px 8px 2px black, 0 0 3px 0px #796851;}

@keyframes move {0% {transform:skewX(2deg) skewY(5deg);} 50% {transform:skewX(-2deg) skewY(0deg);} 100% {transform:skewX(2deg) skewY(5deg);}}
@keyframes move-left {50% {transform:skewX(3deg);}}
@keyframes flame-up {50% {box-shadow:0 0px 0px 3px white, 0 -38px 1px 2px white, 0 -41px 2px 3px gold, 0 -50px 5px 4px #ff6a00, 0 0px 150px 10px #ff6a00, 0 -10px 2px 4px white, 0 -5px 3px 3px white;}}
@keyframes blink {50% {opacity:0.95;}}

/*	tarot cards	*/
.img_wrap	{position:absolute; top:0; left:0; right:0; margin:auto; height:99vh; animation:blur 1.5s linear 2;}
.img		{position:absolute; top:0; left:0; right:0; margin:auto; border-radius:6px 6px 0 0; padding:0; background:url('{_root}data/{ghostimage}.jpg')center no-repeat; height:97vh; width:50vw; filter:grayscale(1) sepia(0.5) brightness(1.3) invert(0.8); opacity:0.5; animation:glitch 2s infinite linear alternate-reverse; background-size:contain;}
@keyframes glitch {0% {transform:translate(1px, 1px) rotate(0deg);} 50% {transform:translate(-1px, 2px) rotate(-1deg);} 100% {transform:translate(1px, -2px) rotate(-1deg);}}

.card_wrap	{text-align:center; margin:0 auto; padding:20px 0; border:5px double #0d0b09; border-radius:3% 10%; background-color:#eceae2e0; transition-duration:0.3s; transition-property:transform;}
.card_wrap:hover, .card_wrap:focus, .card_wrap:active	{cursor:default;}
.card_name	{color:#220b0b; font-size:30px; text-transform:uppercase; text-align:center; font-family:'basteleurbold',serif; margin-top:1em; padding:0 5em 0.5em 5em; line-height:1.25em;}
.card_head	{color:#220b0b; font-size:20px; text-transform:uppercase; font-family:'basteleurmoonlight',serif; display:inline-block; padding:1em; border-top:4px double #766aac; border-bottom:4px double #766aac;}
.card_head span	{font-style:italic;}
.card_artist{margin-top:1.25em;}
.card_art	{max-width:50%; margin:0 1.5em 0.5em 0; float:left;}
.card_info	{color:#220b0b; font-size:26px; margin-top:1em; padding:0 2em; text-align:left;}
.card_info p	{padding:0 0 1.25em 0;}
.card_info p::first-letter	{margin-left:2em;}
.card_info span.dear	{color:#BC003A; font-family:'garamondtitalic',serif; font-size:32px;}
.card_info span.yours	{font-family:'garamondtitalic',serif; display:block; padding:0 0 0.5em 0;}
.card_info span.from	{color:#4900BA; font-family:'basteleurmoonlight',serif; font-size:32px; text-align:left; display:block;}
.card_info span.moon	{color:#4407A2; font-family:'basteleurmoonlight',serif; font-size:19px;}

.card_wrap	{margin:0 auto; width:calc(100% - 4em); height:85vh; animation:show 2.5s steps(40,end);}
@keyframes turn	{0% {transform:rotate(0);} 100% {transform:rotate(360deg);}}
@keyframes show {from {opacity:0;} to {opacity:1;}}
@keyframes half_show {from {opacity:0;} to {opacity:0.5;}}
@keyframes blink_show {0%,30% {opacity:0;} 40%,50% {opacity:0.5;} 60%,70% {opacity:0;} 90%,100% {opacity:0.5;}}
@keyframes slow_show {0%,90% {opacity:0;} 100% {opacity:1;}}
@keyframes slower_show {0%,80% {opacity:0;} 95% {opacity:0.5;} 100% {opacity:1;}}


/*	ghosts	*/
.ghost_handle	{position:absolute; top:18em; left:-100px; width:0; height:0; overflow:visible; animation:run 15s cubic-bezier(.21,.67,.72,.28);}
.ghost_image	{position:relative; top:-100px; left:-100px; width:200px; height:200px; background:url('{_root}data/{ghostimage}.small.jpg') center center / contain no-repeat; filter:grayscale(1) sepia(0.5) brightness(1.3) invert(0.8); opacity:0.2; animation:big 15s linear;}

@keyframes run {0%{left:110vw;} 50%{} 100%{left:-10vw;}}
@keyframes big {0%{} 50%{top:-240px; left:-240px; width:480px; height:480px; opacity:0.5;} 100%{}}



@media (min-width: 360px) and (max-width: 767.98px) {
	.img		{width:100vw; height:55vh;}
	.woe		{width:25vw;}
	.talk_wrap	{min-width:360px; min-height:95vh; padding:0;}
	.talky		{width:50vh;}
	.fog		{background-size:200%;}
	.title_wrap	{width:100vw;}
	.title,.title.s,.title.d,
	.title.p	{font-size:3em; margin:0 auto;}
	.title.d	{margin-top:0.5em;}
	.title.p	{padding-bottom:10px;}
	#letters,
	#letters.sea,
	.card_info span.moon,
	.quest span.quo,
	.fund_img_wrap span,
	.ai,
	.fund_info	{font-size:1em;}
	.quest span.ask	{font-size:0.75em;}
	.say		{font-size:0.85em; padding:15px 5px;}
	.card_wrap	{width:90vw; height:80vh;}
	.card_name	{font-size:1em; margin-top:0.5em; padding:0 1em 0.5em 1em;}
	.card_info	{font-size:1em; margin-top:0; padding:0 1em 1em 1em;}
	.card_head	{font-size:1em; margin:0 0.7em 1em 0.7em;}
	.card_artist{margin-top:1.5em;}
	.fund_img_a,
	.fund_img_b	{max-width:75vw;}
	.fund span	{padding:0px 20px 10px 0;}

}

