	#letters,
	#letters.sea,
	.card_info span.moon,
	.quest span.quo,
	.fund_img_wrap span,
	.ai,
	.fund_info	{font-size:1em;}
	.quest span.ask	{font-size:0.75em;}
	.say		{font-size:0.85em; padding:15px 5px;}
	.card_wrap	{width:90vw; height:80vh;}
	.card_name	{font-size:1em; margin-top:0.5em; padding:0 1em 0.5em 1em;}
	.card_info	{font-size:1em; margin-top:0; padding:0 1em 1em 1em;}
	.card_head	{font-size:1em; margin:0 0.7em 1em 0.7em;}
	.card_artist{margin-top:1.5em;}
	.fund_img_a,
	.fund_img_b	{max-width:75vw;}
	.fund span	{padding:0px 20px 10px 0;}
}
.fog	{background:transparent;}
.talk_wrap	{flex-wrap:wrap;}
.talky	{background-color:transparent; border:0; box-shadow:initial; animation:show 1.5s steps(40,end);}
